    coordinates: [-122.2308, 47.6266] // Medina
  }
];

const PropertyMap = () => {
  const mapContainer = useRef(null);
  const map = useRef(null);


  const formatDealType = (dealType) => {
    return dealType.split('-').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  useEffect(() => {
    if (map.current) return; // Initialize map only once

    // Dynamically import mapbox-gl
    import('mapbox-gl').then((mapboxgl) => {
      mapboxgl.default.accessToken = MAPBOX_TOKEN;

      map.current = new mapboxgl.default.Map({
        container: mapContainer.current,
        style: 'mapbox://styles/mapbox/streets-v12',
        center: [-122.3321, 47.6062],
        zoom: 11
      });

      // Add markers for each property
      mapProperties.forEach((property) => {
        // Create a DOM element for the marker (Simple, clean pin)
        const markerEl = document.createElement('div');
        markerEl.className = 'map-pin';
        markerEl.style.cssText = `
          position: relative;
          width: 24px;
          height: 36px;
          cursor: pointer;
          z-index: 1;
        `;

        // Create the pin shape using CSS
        markerEl.innerHTML = `
          <svg width="24" height="36" viewBox="0 0 24 36" style="filter: drop-shadow(0 2px 4px rgba(0,0,0,0.3));">
            <path d="M12 0C5.373 0 0 5.373 0 12c0 12 12 24 12 24s12-12 12-24C24 5.373 18.627 0 12 0z" 
                  fill="${DEAL_COLORS[property.dealType]}" 
                  stroke="white" 
                  stroke-width="2"/>
            <circle cx="12" cy="12" r="6" fill="white"/>
            <circle cx="12" cy="12" r="3" fill="${DEAL_COLORS[property.dealType]}"/>
          </svg>
        `;

        // Add hover effects that won't cause disappearing
        markerEl.addEventListener('mouseenter', () => {
          markerEl.style.transform = 'scale(1.1)';
          markerEl.style.zIndex = '100';
        });

        markerEl.addEventListener('mouseleave', () => {
          markerEl.style.transform = 'scale(1)';
          markerEl.style.zIndex = '1';
        });

        // Create popup content with enhanced styling
        const popupContent = `
          <div style="
            padding: 0; 
            min-width: 320px; 
            max-width: 350px;
            font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
            box-shadow: 0 8px 32px rgba(0, 0, 0, 0.12);
            border-radius: 12px;
            overflow: hidden;
            border: 1px solid rgba(0, 0, 0, 0.08);
          ">
            <div style="position: relative;">
              <img 
                src="${property.image}" 
                alt="${property.title}" 
                style="width: 100%; height: 180px; object-fit: cover;"
                onerror="this.src='https://via.placeholder.com/350x180?text=Property+Image'"
              />
              <div style="
                position: absolute; 
                top: 12px; 
                left: 12px;
                background: ${DEAL_COLORS[property.dealType]}; 
                color: white; 
                padding: 6px 12px; 
                border-radius: 20px; 
                font-size: 0.75rem; 
                font-weight: 600;
                text-transform: uppercase;
                letter-spacing: 0.5px;
                box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
              ">
                ${formatDealType(property.dealType)}
              </div>
              <div style="
                position: absolute; 
                top: 12px; 
                right: 12px;
                background: rgba(0, 0, 0, 0.7); 
                color: white; 
                padding: 4px 8px; 
                border-radius: 12px; 
                font-size: 0.7rem; 
                font-weight: 500;
              ">
                ${property.category === 'multifamily' ? 'Multi-Family' : 'Single Family'}
              </div>
            </div>

            <div style="padding: 18px;">
              <div style="
                font-size: 1.5rem; 
                font-weight: 700; 
                color: #1e293b; 
                margin-bottom: 8px;
                line-height: 1.2;
              ">
                $${property.price.toLocaleString()}
                ${property.type === 'rent' ? '<span style="font-size: 0.9rem; color: #64748b; font-weight: 400;">/month</span>' : ''}
              </div>

              <div style="
                font-size: 1.1rem; 
                font-weight: 600; 
                color: #374151; 
                margin-bottom: 6px;
                line-height: 1.3;
              ">
                ${property.title}
              </div>

              <div style="
                color: #64748b; 
                font-size: 0.9rem; 
                margin-bottom: 16px;
                display: flex;
                align-items: center;
                gap: 4px;
              ">
                <span style="font-size: 0.8rem;">ÔøΩ</span> ${property.location}
              </div>

              <div style="
                display: grid; 
                grid-template-columns: repeat(3, 1fr); 
                gap: 12px; 
                margin-bottom: 16px;
                padding: 12px;
                background: #f8fafc;
                border-radius: 8px;
              ">
                <div style="text-align: center;">
                  <div style="font-size: 0.75rem; color: #64748b; margin-bottom: 2px;">BEDS</div>
                  <div style="font-weight: 600; color: #1e293b;">${property.bedrooms}</div>
                </div>
                <div style="text-align: center;">
                  <div style="font-size: 0.75rem; color: #64748b; margin-bottom: 2px;">BATHS</div>
                  <div style="font-weight: 600; color: #1e293b;">${property.bathrooms}</div>
                </div>
                <div style="text-align: center;">
                  <div style="font-size: 0.75rem; color: #64748b; margin-bottom: 2px;">SQFT</div>
                  <div style="font-weight: 600; color: #1e293b;">${property.sqft.toLocaleString()}</div>
                </div>
              </div>

              <button 
                style="
                  background: linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%);
                  color: white; 
                  border: none; 
                  padding: 12px 20px; 
                  border-radius: 8px; 
                  font-size: 0.9rem; 
                  font-weight: 600; 
                  cursor: pointer; 
                  width: 100%;
                  transition: all 0.2s ease;
                  box-shadow: 0 2px 4px rgba(59, 130, 246, 0.2);
                "
                onmouseover="this.style.transform='translateY(-1px)'; this.style.boxShadow='0 4px 12px rgba(59, 130, 246, 0.3)';"
                onmouseout="this.style.transform='translateY(0)'; this.style.boxShadow='0 2px 4px rgba(59, 130, 246, 0.2)';"
                onclick="window.location.href='/property/${property.id}'"
              >
                View Property Details
              </button>
            </div>
          </div>
        `;

        const popup = new mapboxgl.default.Popup({
          offset: [0, -45],
          closeButton: true,
          closeOnClick: false,
          className: 'custom-popup',
          maxWidth: '400px'
        }).setHTML(popupContent);

        // Add marker to map
        new mapboxgl.default.Marker(markerEl)
          .setLngLat(property.coordinates)
          .setPopup(popup)
          .addTo(map.current);
      });
    });
  }, []);

  return (
    <MapContainer>
      <div ref={mapContainer} style={{ width: '100%', height: '100%' }} />

      <Legend>
        <h3>Property Legend</h3>

        <LegendSection>
          <h4>Deal Types</h4>
          <LegendItem>
            <ColorDot color={DEAL_COLORS['seller-finance']} />
            <span>Seller Finance</span>
          </LegendItem>
          <LegendItem>
            <ColorDot color={DEAL_COLORS['hybrid']} />
            <span>Hybrid</span>
          </LegendItem>
          <LegendItem>
            <ColorDot color={DEAL_COLORS['subject-to']} />
            <span>Subject To</span>
          </LegendItem>
          <LegendItem>
            <ColorDot color={DEAL_COLORS['cash']} />
            <span>Cash</span>
          </LegendItem>
        </LegendSection>

        <LegendSection>
          <h4>Categories</h4>
          <LegendItem>
            <span style={{ fontSize: '0.8rem', color: '#64748b' }}>üè† Single Family</span>
          </LegendItem>
          <LegendItem>
            <span style={{ fontSize: '0.8rem', color: '#64748b' }}>üè¢ Multi-Family</span>
          </LegendItem>
        </LegendSection>
      </Legend>
    </MapContainer>
  );
};

export default PropertyMap;
