import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const Card = styled.div`
  background: white;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.2s, box-shadow 0.2s;
  cursor: pointer;

  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 8px 20px rgba(0, 0, 0, 0.15);
  }
`;

const ImageContainer = styled.div`
  position: relative;
  height: 200px;
  overflow: hidden;
`;

const PropertyImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const Badge = styled.span`
  position: absolute;
  top: 1rem;
  left: 1rem;
  background: ${props => props.type === 'sale' ? '#10b981' : props.type === 'rent' ? '#3b82f6' : '#f59e0b'};
  color: white;
  padding: 0.25rem 0.75rem;
  border-radius: 20px;
  font-size: 0.8rem;
  font-weight: 500;
  text-transform: uppercase;
`;

const Content = styled.div`
  padding: 1.5rem;
`;

const Price = styled.div`
  font-size: 1.5rem;
  font-weight: 700;
  color: #3b82f6;
  margin-bottom: 0.5rem;
`;

const Title = styled.h3`
  font-size: 1.1rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: #1e293b;
`;

const Location = styled.p`
  color: #64748b;
  margin-bottom: 1rem;
  font-size: 0.9rem;
`;

const Features = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 1rem;
  font-size: 0.9rem;
  color: #374151;
`;

const Feature = styled.span`
  display: flex;
  align-items: center;
  gap: 0.25rem;
`;

const PropertyCard = ({ property }) => {
  return (
    <Link to={`/property/${property.id}`} style={{ textDecoration: 'none' }}>
      <Card>
        <ImageContainer>
          <PropertyImage 
            src={property.image} 
            alt={property.title}
            onError={(e) => {
              e.target.src = 'https://via.placeholder.com/400x200?text=Property+Image';
            }}
          />
          <Badge type={property.type}>{property.type}</Badge>
        </ImageContainer>
        <Content>
          <Price>
            ${property.price.toLocaleString()}
            {property.type === 'rent' && <span style={{ fontSize: '0.9rem', color: '#64748b' }}>/month</span>}
          </Price>
          <Title>{property.title}</Title>
          <Location>üìç {property.location}</Location>
          <Features>
            <Feature>üõèÔ∏è {property.bedrooms} bed</Feature>
            <Feature>üöø {property.bathrooms} bath</Feature>
            <Feature>üìê {property.sqft} sqft</Feature>
          </Features>
        </Content>
      </Card>
    </Link>
  );
};

export default PropertyCard;
