import React from 'react';
import { useParams, Link } from 'react-router-dom';
import styled from 'styled-components';
import { useListings } from '../context/ListingsContext';

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 6rem 2rem 2rem;
`;

const PropertyHeader = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 2rem;
  margin-bottom: 2rem;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const ImageGallery = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 1rem;
  height: 400px;
`;

const MainImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 12px;
`;

const ThumbnailGrid = styled.div`
  display: grid;
  grid-template-rows: repeat(2, 1fr);
  gap: 1rem;
`;

const Thumbnail = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 8px;
`;

const PropertyInfo = styled.div`
  background: white;
  padding: 2rem;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  height: fit-content;
`;

const Price = styled.div`
  font-size: 2.5rem;
  font-weight: 700;
  color: #3b82f6;
  margin-bottom: 1rem;
`;

const Title = styled.h1`
  font-size: 1.8rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: #1e293b;
`;

const Location = styled.p`
  color: #64748b;
  font-size: 1.1rem;
  margin-bottom: 2rem;
`;

const FeaturesList = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 1rem;
  margin-bottom: 2rem;
`;

const Feature = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 500;
  color: #374151;
`;

const ContactButton = styled.button`
  width: 100%;
  padding: 1rem;
  background: #3b82f6;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  margin-bottom: 1rem;
  transition: background-color 0.2s;

  &:hover {
    background: #2563eb;
  }
`;

const DealTermsSection = styled.div`
  background: #f8fafc;
  padding: 1.5rem;
  border-radius: 12px;
  margin-top: 2rem;
  border: 1px solid #e2e8f0;
`;

const DealTermsTitle = styled.h3`
  font-size: 1.2rem;
  font-weight: 600;
  color: #1e293b;
  margin-bottom: 1rem;
`;

const TermsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
`;

const TermItem = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0.75rem;
  background: white;
  border-radius: 6px;
  border: 1px solid #e5e7eb;
`;

const TermLabel = styled.span`
  color: #64748b;
  font-size: 0.9rem;
`;

const TermValue = styled.span`
  color: #1e293b;
  font-weight: 600;
  font-size: 0.9rem;
`;

const Description = styled.div`
  background: white;
  padding: 2rem;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  margin-top: 2rem;

  h2 {
    margin-bottom: 1rem;
    color: #1e293b;
  }

  p {
    line-height: 1.7;
    color: #374151;
  }
`;

const FilesSection = styled.div`
  margin-top: 2rem;
`;

const SectionTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #1e293b;
  margin-bottom: 1.5rem;
  border-bottom: 2px solid #e2e8f0;
  padding-bottom: 0.5rem;
`;

const PhotoGallery = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 1rem;
  margin-bottom: 2rem;
`;

const PhotoItem = styled.div`
  position: relative;
  aspect-ratio: 4/3;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  cursor: pointer;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 15px rgba(0, 0, 0, 0.15);
  }

  transition: all 0.2s;
`;

const PhotoImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const DocumentsList = styled.div`
  display: grid;
  gap: 0.75rem;
`;

const DocumentItem = styled.a`
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 1rem;
  background: white;
  border: 1px solid #e5e7eb;
  border-radius: 8px;
  text-decoration: none;
  color: #374151;
  transition: all 0.2s;

  &:hover {
    background: #f9fafb;
    border-color: #3b82f6;
    color: #3b82f6;
  }
`;

const DocumentIcon = styled.div`
  font-size: 1.5rem;
`;

const DocumentInfo = styled.div`
  flex: 1;
`;

const DocumentName = styled.div`
  font-weight: 500;
  margin-bottom: 0.25rem;
`;

const DocumentSize = styled.div`
  font-size: 0.875rem;
  color: #64748b;
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 2rem;
  color: #64748b;
  background: #f8fafc;
  border-radius: 8px;
  border: 1px dashed #d1d5db;
`;

const PropertyDetail = () => {
  const { id } = useParams();
  const { properties } = useListings();
  const property = properties.find(p => p.id === parseInt(id));

  if (!property) {
    return (
      <Container>
        <div style={{ textAlign: 'center', padding: '4rem 2rem' }}>
          <h1>Property Not Found</h1>
          <p>The property you're looking for doesn't exist.</p>
          <Link to="/listings" className="btn btn-primary" style={{ marginTop: '2rem' }}>
            Back to Listings
          </Link>
        </div>
      </Container>
    );
  }

  return (
    <Container>
      <Link to="/listings" style={{ 
        color: '#3b82f6', 
        textDecoration: 'none', 
        marginBottom: '2rem', 
        display: 'inline-block',
        fontWeight: '500'
      }}>
        ‚Üê Back to Listings
      </Link>

      <PropertyHeader>
        <ImageGallery>
          <MainImage 
            src={property.propertyPhotos?.[0]?.url || property.image || property.images?.[0]} 
            alt={property.title}
            onError={(e) => {
              e.target.src = 'https://via.placeholder.com/800x400?text=Property+Image';
            }}
          />
          <ThumbnailGrid>
            <Thumbnail 
              src={property.images?.[1] || property.image} 
              alt={property.title}
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/400x200?text=Image+2';
              }}
            />
            <Thumbnail 
              src={property.images?.[2] || property.image} 
              alt={property.title}
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/400x200?text=Image+3';
              }}
            />
          </ThumbnailGrid>
        </ImageGallery>

        <PropertyInfo>
          <Price>
            ${property.price.toLocaleString()}
            {property.type === 'rent' && <span style={{ fontSize: '1rem', color: '#64748b' }}>/month</span>}
          </Price>
          <Title>{property.title}</Title>
          <Location>üìç {property.location}</Location>

          <FeaturesList>
            {property.bedrooms && <Feature>üõèÔ∏è {property.bedrooms} Bedrooms</Feature>}
            {property.bathrooms && <Feature>üöø {property.bathrooms} Bathrooms</Feature>}
            {property.sqft && <Feature>üìê {parseInt(property.sqft).toLocaleString()} sqft</Feature>}
            {property.units && property.units > 1 && <Feature>üè† {property.units} Units</Feature>}
            {property.yearBuilt && <Feature>üèóÔ∏è Built in {property.yearBuilt}</Feature>}
            {property.parking && <Feature>üöó {property.parking} Parking Space</Feature>}
            <Feature>üè∑Ô∏è For {property.type === 'sale' ? 'Sale' : 'Rent'}</Feature>
            {property.dealType && (
              <Feature style={{background: '#3b82f6', color: 'white', padding: '0.5rem', borderRadius: '6px'}}>
                üíº {property.dealType.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')} Deal
              </Feature>
            )}
            {property.category && (
              <Feature style={{background: '#64748b', color: 'white', padding: '0.5rem', borderRadius: '6px'}}>
                {property.category === 'multifamily' ? 'üè¢ Multi-Family' : 'üè† Single Family'}
              </Feature>
            )}
          </FeaturesList>

          <ContactButton>
            Contact Agent
          </ContactButton>

          <ContactButton style={{ background: '#10b981' }}>
            Schedule Tour
          </ContactButton>

          {/* Deal-specific terms */}
          {property.dealType && property.dealType !== 'cash' && (
            <DealTermsSection>
              <DealTermsTitle>
                {property.dealType === 'hybrid' ? 'Deal Terms (Seller Finance & Subject-To)' : 
                 property.dealType === 'seller-finance' ? 'Seller Finance Terms' : 
                 'Subject-To Terms'}
              </DealTermsTitle>
              <TermsGrid>
                {(property.dealType === 'seller-finance' || property.dealType === 'hybrid') && (
                  <>
                    {property.downPayment && (
                      <TermItem>
                        <TermLabel>Down Payment:</TermLabel>
                        <TermValue>${parseInt(property.downPayment).toLocaleString()}</TermValue>
                      </TermItem>
                    )}
                    {property.interestRate && (
                      <TermItem>
                        <TermLabel>Interest Rate:</TermLabel>
                        <TermValue>{property.interestRate}%</TermValue>
                      </TermItem>
                    )}
                    {property.loanTerm && (
                      <TermItem>
                        <TermLabel>Loan Term:</TermLabel>
                        <TermValue>{property.loanTerm} years</TermValue>
                      </TermItem>
                    )}
                    {property.monthlyPayment && (
                      <TermItem>
                        <TermLabel>Monthly Payment:</TermLabel>
                        <TermValue>${parseInt(property.monthlyPayment).toLocaleString()}</TermValue>
                      </TermItem>
                    )}
                    {property.balloonPayment && (
                      <TermItem>
                        <TermLabel>Balloon Payment:</TermLabel>
                        <TermValue>${parseInt(property.balloonPayment).toLocaleString()}</TermValue>
                      </TermItem>
                    )}
                  </>
                )}
                {(property.dealType === 'subject-to' || property.dealType === 'hybrid') && (
                  <>
                    {property.existingMortgageBalance && (
                      <TermItem>
                        <TermLabel>Existing Mortgage:</TermLabel>
                        <TermValue>${parseInt(property.existingMortgageBalance).toLocaleString()}</TermValue>
                      </TermItem>
                    )}
                    {property.monthlyMortgagePayment && (
                      <TermItem>
                        <TermLabel>Monthly Mortgage:</TermLabel>
                        <TermValue>${parseInt(property.monthlyMortgagePayment).toLocaleString()}</TermValue>
                      </TermItem>
                    )}
                  </>
                )}
              </TermsGrid>
            </DealTermsSection>
          )}
        </PropertyInfo>
      </PropertyHeader>

      <Description>
        <h2>About This Property</h2>
        <p>{property.description}</p>
      </Description>

      {(property.propertyPhotos?.length > 0 || property.financialDocuments?.length > 0) && (
        <FilesSection>
          {property.propertyPhotos?.length > 0 && (
            <div>
              <SectionTitle>Property Photos</SectionTitle>
              <PhotoGallery>
                {property.propertyPhotos.map((photo, index) => (
                  <PhotoItem key={index} onClick={() => window.open(photo.url, '_blank')}>
                    <PhotoImage src={photo.url} alt={`Property photo ${index + 1}`} />
                  </PhotoItem>
                ))}
              </PhotoGallery>
            </div>
          )}

          {property.financialDocuments?.length > 0 && (
            <div>
              <SectionTitle>Financial Documents</SectionTitle>
              <DocumentsList>
                {property.financialDocuments.map((doc, index) => (
                  <DocumentItem 
                    key={index} 
                    href={doc.url} 
                    download={doc.name}
                    target="_blank"
                  >
                    <DocumentIcon>üìÑ</DocumentIcon>
                    <DocumentInfo>
                      <DocumentName>{doc.name}</DocumentName>
                      <DocumentSize>
                        {doc.size ? `${(doc.size / 1024 / 1024).toFixed(2)} MB` : 'Document'}
                      </DocumentSize>
                    </DocumentInfo>
                  </DocumentItem>
                ))}
              </DocumentsList>
            </div>
          )}

          {!property.propertyPhotos?.length && !property.financialDocuments?.length && (
            <EmptyState>
              <p>No additional files uploaded for this property.</p>
            </EmptyState>
          )}
        </FilesSection>
      )}
    </Container>
  );
};

export default PropertyDetail;
